import React, { forwardRef } from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import Modal from "@material-ui/core/Modal";
import Button from "@material-ui/core/Button";
import Chip from "@material-ui/core/Chip";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogTitle from "@material-ui/core/DialogTitle";
import Fab from "@material-ui/core/Fab";
import EmailIcon from "@material-ui/icons/Email";
import TextField from "@material-ui/core/TextField";
import InputBase from "@material-ui/core/InputBase";
import { Divider } from "@material-ui/core";
import FormControl from "@material-ui/core/FormControl";
import Input from "@material-ui/core/Input";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import Select from "@material-ui/core/Select";
import store from "../../../store";
import axios from "axios";
import Grid from "@material-ui/core/Grid";


function rand() {
  return Math.round(Math.random() * 20) - 10;
}

function getModalStyle() {
  const top = 50 + rand();
  const left = 50 + rand();

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`
  };
}

const styles = theme => ({
  root: {
    flexGrow: 1,
    display: "flex",
    flexWrap: "wrap"
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    boxShadow: theme.shadows[5],
    padding: theme.spacing(4),
    paddingLeft: theme.spacing(4),
    outline: "none"
  },
  formControl: {
    margin: 2,
    minWidth: 120,
    maxWidth: 300
  },
  chips: {
    display: "flex",
    flexWrap: "wrap"
  },
  closeButton: {
    position: "absolute",
    right: theme.spacing(),
    top: theme.spacing(),
    color: theme.palette.grey[500]
  },
  sendButton: {
    backgroundColor: "#094183",
    "&:hover": {
      backgroundColor: "#4074B2"
    }
  },
  discardButton: {
    color: "#094183"
  },
  editButton: {
    marginTop: 0,
    color: "#094183",
  },
  textField: {
    marginLeft: 0,
    marginRight: 15,
    width: "45%"
  },
  emailTextField: {
    marginLeft: 0,
    marginRight: 15,
    width: "83%"
  },
  selectField: {
      width: "83%"
  }
});

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: "87%"
    }
  },
  anchorOrigin: {
    vertical: "bottom",
    horizontal: "left"
  },
  transformOrigin: {
    vertical: "top",
    horizontal: "left"
  },
  getContentAnchorEl: null,
  dense: "true"
};

class EditClientModal extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            open: false,
            fullWidth: true,
            maxWidth: "lg",
            technicalAbility: this.props.client.technicalAbility
        };
    }

    _handleEdit = () => {
        this.setState({open: true});
    };

    _handleDiscard = () => {
        this.setState({open: false});
    };

    _handleTechnicalAbilityUpdate = (e) => {
        this.setState({ technicalAbility: e.target.value});
    };

    render() {
        const { classes, client } = this.props;

        return (
        <div>
            <Button onClick={this._handleEdit}
                    className={classes.editButton}>
                Edit
            </Button>
            <Dialog
                fullWidth={this.state.fullWidth}
                maxWidth={this.state.maxWidth}
                open={this.state.open}
                onClose={this._handleDiscard}
                aria-labelledby="max-width-dialog-title"
            >
            <DialogTitle onClose={this._handleDiscard}>Edit Client Profile</DialogTitle>

            <Divider />

            <DialogContent>
                <Grid container spacing={3}>
                    <Grid item xs={6}>
                        <h5 style={{ marginBottom: 0}}>Primary Contact</h5>
                        <form className={classes.container} noValidate autoComplete="off">
                            <TextField
                                id="firstName"
                                label="First Name"
                                defaultValue={client.firstName}
                                className={classes.textField}
                                margin="normal"
                            />
                            <TextField
                                id="lastName"
                                label="Last Name"
                                defaultValue={client.lastName}
                                className={classes.textField}
                                margin="normal"
                            />
                            <TextField
                                id="email"
                                label="Email Address"
                                defaultValue={client.email}
                                className={classes.emailTextField}
                                margin="normal"
                            />
                            <TextField
                                id="contactNumber"
                                label="Mobile Number"
                                defaultValue={client.contactNumber}
                                className={classes.textField}
                                margin="normal"
                            />
                            <TextField
                                id="orgNumber"
                                label="Office Number"
                                defaultValue={client.orgNumber}
                                className={classes.textField}
                                margin="normal"
                                InputLabelProps={{ shrink: true }}
                            />
                            <TextField
                                id="technicalAbility"
                                select
                                label="Technical Ability"
                                className={classes.emailTextField}
                                defaultValue={this.state.technicalAbility}
                                value={this.state.technicalAbility}
                                onChange={e => this._handleTechnicalAbilityUpdate(e)}
                                SelectProps={{
                                    enuProps: {
                                        className: classes.menu,
                                    },
                                }}
                                margin="normal"
                            >
                                <MenuItem value={1}>1</MenuItem>
                                <MenuItem value={2}>2</MenuItem>
                                <MenuItem value={3}>3</MenuItem>
                                <MenuItem value={4}>4</MenuItem>
                                <MenuItem value={5}>5</MenuItem>
                                <MenuItem value={6}>6</MenuItem>
                                <MenuItem value={7}>7</MenuItem>
                                <MenuItem value={8}>8</MenuItem>
                                <MenuItem value={9}>9</MenuItem>
                                <MenuItem value={10}>10</MenuItem>
                            </TextField>
                        </form>
                    </Grid>
                    <Grid item xs={6}>
                        <h5 style={{ marginBottom: 0}}>Secondary Contact</h5>
                        <form className={classes.container} noValidate autoComplete="off">
                            <TextField
                                id="secondaryContctFName"
                                label="First Name"
                                defaultValue={client.secondaryContactFirstName}
                                className={classes.textField}
                                margin="normal"
                            />
                            <TextField
                                id="secondaryContactLastName"
                                label="Last Name"
                                defaultValue={client.secondaryContactLastName}
                                className={classes.textField}
                                margin="normal"
                            />
                            <TextField
                                id="secondaryContactEmail"
                                label="Email Address"
                                defaultValue={client.secondaryContactEmail}
                                className={classes.emailTextField}
                                margin="normal"
                            />
                            <TextField
                                id="secondaryContactNumber"
                                label="Mobile Number"
                                defaultValue={client.secondaryContactNumber}
                                className={classes.textField}
                                margin="normal"
                            />
                        </form>
                    </Grid>
                </Grid>

               
            </DialogContent>
            <Divider />

            <DialogActions>
                <Button
                variant="contained"
                color="primary"
                onClick={this.handleSendEmail}
                className={classes.sendButton}
                >
                Send
                </Button>
                <Button
                onClick={this.handleClose}
                color="primary"
                className={classes.discardButton}
                >
                Discard
                </Button>
            </DialogActions>
            </Dialog>
        </div>
        );
    }
}

export default withStyles(styles)(EditClientModal);
