/**
 * This component contains all clients. It is entered via the "View Clients" link on the app drawer.
 * Author: Reyna Tan
 * Date: 09/10/2019
 */

import React, { PureComponent } from 'react';
import store from "../../../../store";
import {getAllClients} from "../../../../api";
import {
    getAllClientsAction,
} from "../../../../store/actionCreators";
import MaterialTable from 'material-table';
import ClientPageModal from "./ClientPageModal";

class AllClients extends PureComponent {

    constructor(props) {
        super(props);

        this.state = store.getState();

        this._handleStoreChange = this._handleStoreChange.bind(this);
        store.subscribe(this._handleStoreChange);
    }

    _handleStoreChange() {
        this.setState(store.getState());
    }

    async _reqTodoList() {
        const clients = await getAllClients();
        const getAllClientsAct = getAllClientsAction(clients);
        store.dispatch(getAllClientsAct);
    }

    componentDidMount() {
        this._reqTodoList();
    }

    componentWillReceiveProps(nextProps) {
        console.log(nextProps.currentPage);
    }

    _formatDataIntoTableList() {
        const {clients} = this.state;

        let clientList = [];

        clients.forEach(c => {
            let nextClient = {
                name: c.firstName + " " + c.lastName,
                email: c.email,
                contactNumber: c.contactNumber,
                organisation: c.organisation.name,
                technicalAbility: c.technicalAbility,
                flag: c.flag,
                client: c
            }

            clientList.push(nextClient);
            
        })

        return clientList;
    }


    // Render clientPageModal
    _handleClick(client) {
       return ( 
            <ClientPageModal 
                client={client}
                objType={objType}
                objID={objID}
            />
        )
    }

    render() {
        
        return (
            <MaterialTable
                title="All Projects"
                columns={[
                    { title: 'Year', field: 'year', filterCellStyle:{maxWidth:50} },
                    { title: 'Project Name', field: 'name' },
                    { title: 'Client', field: 'client' },
                    { title: 'Description', field: 'outlineOfProject', filtering: false },
                    { title: 'Status', field: 'status', lookup: { new: 'New', inProgress: 'In Progress', completed: 'Completed'}, filterCellStyle:{paddingTop:0} },
                    { title: 'Subject', field: 'subjectId', filterCellStyle:{maxWidth:50}, lookup: this._getSubjectFilterLookup(), filterCellStyle:{paddingTop:0} },
                    { title: 'Supervisor', field: 'supervisor', lookup: this._getSupervisorFilterLookup(), filterCellStyle:{paddingTop:0} },
                ]}
                data={this._formatDataIntoTableList()}
                options={{
                    filtering: true,
                    exportButton: true,
                }}
                onRowClick={(event, rowData) => this._handleClick(rowData._id)}
            />
        );
    }
}

export default (AllClients);
