import React, { forwardRef } from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import Modal from "@material-ui/core/Modal";
import Button from "@material-ui/core/Button";
import Chip from "@material-ui/core/Chip";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogTitle from "@material-ui/core/DialogTitle";
import Fab from "@material-ui/core/Fab";
import EmailIcon from "@material-ui/icons/Email";
import TextField from "@material-ui/core/TextField";
import InputBase from "@material-ui/core/InputBase";
import { Divider } from "@material-ui/core";
import FormControl from "@material-ui/core/FormControl";
import Input from "@material-ui/core/Input";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import Select from "@material-ui/core/Select";
import store from "../../../store";
import axios from "axios";

function rand() {
  return Math.round(Math.random() * 20) - 10;
}

function getModalStyle() {
  const top = 50 + rand();
  const left = 50 + rand();

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`
  };
}

const styles = theme => ({
  root: {
    flexGrow: 1,
    display: "flex",
    flexWrap: "wrap"
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    boxShadow: theme.shadows[5],
    padding: theme.spacing(4),
    paddingLeft: theme.spacing(4),
    outline: "none"
  },
  formControl: {
    margin: 2,
    minWidth: 120,
    maxWidth: 300
  },
  chips: {
    display: "flex",
    flexWrap: "wrap"
  },
  selectField: {
    [theme.breakpoints.up("md")]: {
      width: 1118
    },
    [theme.breakpoints.down("md")]: {
      width: 480
    }
  },
  closeButton: {
    position: "absolute",
    right: theme.spacing(),
    top: theme.spacing(),
    color: theme.palette.grey[500]
  },
  sendButton: {
    backgroundColor: "#094183",
    "&:hover": {
      backgroundColor: "#4074B2"
    }
  },
  discardButton: {
    color: "#094183"
  },
  editButton: {
    marginTop: 0,
    color: "#094183",
  },
});

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: "87%"
    }
  },
  anchorOrigin: {
    vertical: "bottom",
    horizontal: "left"
  },
  transformOrigin: {
    vertical: "top",
    horizontal: "left"
  },
  getContentAnchorEl: null,
  dense: "true"
};


class EditClientModal extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            open: false,
            fullWidth: true,
            maxWidth: "lg",
        };
    }

    _handleEdit = () => {
        this.setState({open: true});
    };

    _handleDiscard = () => {
        this.setState({open: false});
    };

    render() {
        const { classes } = this.props;

        return (
        <div>
            <Button onClick={this._handleEdit}
                    className={classes.editButton}>
                Edit
            </Button>
            <Dialog
                fullWidth={this.state.fullWidth}
                maxWidth={this.state.maxWidth}
                open={this.state.open}
                onClose={this._handleDiscard}
                aria-labelledby="max-width-dialog-title"
            >
            <DialogTitle onClose={this._handleDiscard}>Edit Client</DialogTitle>

            <Divider />

            <DialogContent>
                <form className={classes.container} noValidate autoComplete="off">
                    <TextField
                        id="standard-helperText"
                        label="First Name"
                        defaultValue="Default Value"
                        className={classes.textField}
                        helperText="Some important text"
                        margin="normal"
                    />
                </form>
            </DialogContent>
            <Divider />

            <DialogActions>
                <Button
                variant="contained"
                color="primary"
                onClick={this.handleSendEmail}
                className={classes.sendButton}
                >
                Send
                </Button>
                <Button
                onClick={this.handleClose}
                color="primary"
                className={classes.discardButton}
                >
                Discard
                </Button>
            </DialogActions>
            </Dialog>
        </div>
        );
    }
}

export default withStyles(styles)(EditClientModal);
